#! /usr/bin/python# generates a single triggerVisitEvent and a related mops1Eventimport lsst.events as eventsimport lsst.mwi.data as datap# import sysif __name__ == "__main__":    x = events.EventTransmitter("lsst8.ncsa.uiuc.edu", "triggerVisitEvent,mops1Event")    image = "704893 333.882541667 -17.7351666667 i 52795.5802196"#    imageData = sys.stdin.readlines()#    for image in imageData:#        if image[0] == "#":#            continue            d = image.split()            root = datap.SupportFactory.createPropertyNode("root")    exposureId = datap.DataProperty("exposureId", int( d[0] ))    visitId = datap.DataProperty("visitId", int( d[0] ))    ra = datap.DataProperty("FOVRA", float( d[1] ))    dec = datap.DataProperty("FOVDec", float( d[2] ))    filter = datap.DataProperty("filterName", d[3] )    visitTime = datap.DataProperty("visitTime", float( d[4] ))    root.addProperty(exposureId)    root.addProperty(visitId)    root.addProperty(ra)    root.addProperty(dec)    root.addProperty(filter)    root.addProperty(visitTime)        #print exposure, ra, dec, filter, float( d[4] )    x.publish("log", root)